import React, { useState } from 'react';
import Question from '../data/questions.js'

const Quizz = (props) => {
  const questions = Question().sort(() => Math.random() - 0.5).slice(0,props.numberOfQuestions)
	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);
  const [wrongAnswer, setWrongAnswer] = useState([])

	const handleAnswerOptionClick = (isCorrect) => {
		if (isCorrect) {
			setScore(score + 1);
		}else {
      setWrongAnswer(wrongAnswer.concat([questions[currentQuestion].questionText,
        questions[currentQuestion].answerOptions.find((element) => element.isCorrect === true)]
      )
      )
    }

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
  return (
    <div className='quizz'>
      {showScore ? (
				<div className='score-section'>
					You scored {score} out of {questions.length}
          {console.log(wrongAnswer)}
				</div>
			) : (
				<>
					<div className='question-section'>
						<div className='question-count'>
							<span>Question {currentQuestion + 1}</span>/{questions.length}
						</div>
						<div className='question-text'>{questions[currentQuestion].questionText}</div>
					</div>
					<div className='answer-section'>
						{questions[currentQuestion].answerOptions.map((answerOption) => (
							<button onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}>{answerOption.answerText}</button>
						))}
					</div>
				</>
			)}

    </div>
  )
}

export default Quizz
