import React, { useMemo, useState } from 'react';
import Quizz from './components/Quizz.jsx'
import { questions } from './data/questions.js'

export default function App() {
  const [numberOfQuestions, setNumberOfQuestions] = useState()
  const handleChange = (e) => {
    setNumberOfQuestions(e.target.value)
  }
  const questionList = useMemo(() =>  questions.sort(() => Math.random() - 0.5).slice(0, numberOfQuestions),[numberOfQuestions])
	return (
		<div className='quizz'>
      {numberOfQuestions?(<Quizz questionList={questionList}/>):(
        <div className='score-section'>
        <form>
          <h3>How many questions you want to get this test</h3>
          <select onChange={handleChange}>
            <option value="Choice">Choice</option>
            <option value="45">45</option>
            <option value="60">60</option>
            <option value="90">90</option>
          </select>
        </form> 
        </div>)
      }
		</div>
	);
}
